import axios from 'axios';
const API = axios.create({ baseURL: 'http://localhost:5000/api' });

export const getProducts = () => API.get('/products');
export const getCart = () => API.get('/cart');
export const addToCart = (productId, qty) => API.post('/cart', { productId, qty });
export const removeFromCart = (id) => API.delete(`/cart/${id}`);
export const checkout = (cartItems, user) => API.post('/checkout', { cartItems, name: user.name, email: user.email });
export const login = (name, email) => API.post('/users/login', { name, email });
