import React, { useEffect, useState } from "react";
import { ShoppingCart, Plus, TrendingUp } from "lucide-react";
import { useNavigate } from "react-router-dom"; // ✅ Add this for navigation
import "./styles.css";

export default function ProductList() {
  const navigate = useNavigate(); // ✅ Initialize navigate

  const [products, setProducts] = useState([
    {
      id: 1,
      name: "Wireless Headphones",
      price: 2999,
      image: "/images/headphones.jpg", // ✅ Use absolute path from public folder
      tag: "Trending",
    },
    {
      id: 2,
      name: "Smart Watch",
      price: 4499,
      image: "/images/smartwatch.jpg",
      tag: "New",
    },
    {
      id: 3,
      name: "Laptop Backpack",
      price: 1299,
      image: "/images/backpack.jpg",
    },
    {
      id: 4,
      name: "USB-C Cable",
      price: 399,
      image: "/images/usb-c-cable.jpg",
    },
    {
      id: 5,
      name: "Phone Stand",
      price: 699,
      image: "/images/phone-stand.jpg",
      tag: "Sale",
    },
    {
      id: 6,
      name: "Bluetooth Speaker",
      price: 1899,
      image: "/images/speaker.jpg",
    },
  ]);

  const [cartCount, setCartCount] = useState(0);

  // Optional: Simulate API fetch (commented out if API not set up)
  useEffect(() => {
    // Example API fetch if you have axios or fetch
    // fetch("/api/products")
    //   .then((res) => res.json())
    //   .then((data) => setProducts(data))
    //   .catch((err) => console.error(err));
  }, []);

  const addToCart = (productId) => {
    setCartCount((prev) => prev + 1);

    // ✅ Toast notification
    const toast = document.createElement("div");
    toast.className = "toast-notification";
    toast.textContent = "✓ Added to cart!";
    document.body.appendChild(toast);

    setTimeout(() => toast.classList.add("show"), 10);
    setTimeout(() => {
      toast.classList.remove("show");
      setTimeout(() => toast.remove(), 300);
    }, 2000);
  };

  const goToCart = () => {
    navigate("/cart"); // ✅ Navigate properly
  };

  return (
    <div className="page-wrapper">
      {/* Header */}
      <header className="header">
        <div className="header-container">
          <div className="header-content">
            <div className="brand-section">
              <div className="brand-icon">
                <TrendingUp className="icon" />
              </div>
              <div className="brand-text">
                <h1 className="brand-title">Vibe Commerce</h1>
                <p className="brand-subtitle">Shop the latest trends</p>
              </div>
            </div>

            <button onClick={goToCart} className="cart-button">
              <ShoppingCart className="cart-icon" />
              <span>Cart</span>
              {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="main-content">
        <div className="hero-section">
          <h2 className="hero-title">Discover Amazing Products</h2>
          <p className="hero-subtitle">
            Curated collection of trending items just for you
          </p>
        </div>

        {/* Product Grid */}
        <div className="product-grid">
          {products.map((product, index) => (
            <div
              key={product.id}
              className="product-card"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <div className="product-image-wrapper">
                <img
                  src={product.image}
                  alt={product.name}
                  className="product-image"
                />
                {product.tag && (
                  <span
                    className={`product-tag tag-${product.tag.toLowerCase()}`}
                  >
                    {product.tag}
                  </span>
                )}
              </div>

              <div className="product-details">
                <h3 className="product-name">{product.name}</h3>
                <div className="product-info">
                  <p className="product-price">
                    ₹{product.price.toLocaleString()}
                  </p>
                  <span className="product-delivery">Free Delivery</span>
                </div>

                <button
                  onClick={() => addToCart(product.id)}
                  className="add-to-cart-btn"
                >
                  <Plus className="plus-icon" />
                  <span>Add to Cart</span>
                </button>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <p>© 2025 Vibe Commerce. Bringing you the best shopping experience.</p>
        </div>
      </footer>
    </div>
  );
}
