import React, { useState, useEffect } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";
import ReceiptModal from "./ReceiptModal";
import "./styles.css";

export default function CheckoutPage() {
  const [user, setUser] = useState(null);
  const [receipt, setReceipt] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ðŸ§  Fetch user details from localStorage (saved at login)
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) setUser(storedUser);
  }, []);

  const handleCheckout = async () => {
    try {
      setLoading(true);

      const token = localStorage.getItem("token");
      if (!token) {
        alert("Please log in before checkout.");
        navigate("/login");
        return;
      }

      // ðŸ›’ Get user's cart items
      const cartRes = await API.get("/cart", {
        headers: { Authorization: `Bearer ${token}` },
      });

      // ðŸ’³ Prepare payload
      const payload = {
        cartItems: cartRes.data.items,
        name: user?.name,
        email: user?.email,
      };

      // ðŸ§¾ Send checkout request
      const res = await API.post("/checkout", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      console.log("Checkout successful:", res.data);
      setReceipt(res.data);
      alert("Checkout successful!");
    } catch (err) {
      console.error("Checkout failed:", err);
      alert(err.response?.data?.error || "Checkout failed.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>

      <p><strong>Name:</strong> {user?.name || "Loading..."}</p>
      <p><strong>Email:</strong> {user?.email || "Loading..."}</p>

      <button onClick={handleCheckout} disabled={loading}>
        {loading ? "Processing..." : "Generate Bill"}
      </button>

      {receipt && (
        <ReceiptModal
          receipt={receipt}
          onConfirm={() => navigate("/ProductList")}
        />
      )}
    </div>
  );
}
