import React, { useEffect, useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";
import "./styles.css";

export default function ProductList() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    API.get("/products")
      .then((res) => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  const addToCart = async (productId, productName) => {
    try {
      await API.post("/cart", { productId, qty: 1 });
      setSuccessMessage(`âœ… ${productName} added to cart!`);
      setTimeout(() => setSuccessMessage(""), 3000);
    } catch (error) {
      console.error("Failed to add item to cart:", error);
      alert("Failed to add item to cart. Please try again.");
    }
  };

  if (loading) {
    return (
      <div className="page-container">
        <div className="goto-cart">ðŸ›’ Go to Cart</div>
        <h2>Available Products</h2>
        <div className="product-grid">
          {[...Array(6)].map((_, index) => (
            <div key={index} className="product-card skeleton">
              <div style={{height: "200px", backgroundColor: "#e5e7eb", borderRadius: "12px"}}></div>
              <div style={{height: "20px", backgroundColor: "#e5e7eb", margin: "10px 0", borderRadius: "4px"}}></div>
              <div style={{height: "24px", backgroundColor: "#e5e7eb", margin: "10px 0", borderRadius: "4px"}}></div>
              <div style={{height: "44px", backgroundColor: "#e5e7eb", borderRadius: "50px"}}></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      {successMessage && <div className="success-message">{successMessage}</div>}

      <button className="goto-cart" onClick={() => navigate("/cart")}>
        ðŸ›’ Go to Cart
      </button>

      <h2>Available Products</h2>

      <div className="product-grid">
        {products.map((p) => (
          <div key={p.id} className="product-card">
            <img src={p.image} alt={p.name} />
            <h3>{p.name}</h3>
            <p>â‚¹{p.price}</p>
            <button onClick={() => addToCart(p.id, p.name)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
