import React, { useEffect, useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

export default function ProductList() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  // Sample product data using your local images
  const sampleProducts = [
    {
      id: 1,
      name: "Wireless Headphones",
      price: 2999,
      image: "/images/headphones.jpg"
    },
    {
      id: 2,
      name: "Smart Watch",
      price: 5999,
      image: "/images/smartwatch.jpg"
    },
    {
      id: 3,
      name: "Laptop Backpack",
      price: 1999,
      image: "/images/backpack.jpg"
    },
    {
      id: 4,
      name: "Phone Stand",
      price: 1499,
      image: "/images/phone-stand.jpg"
    },
    {
      id: 5,
      name: "USB-C Cable",
      price: 799,
      image: "/images/add-e-cable.jpg"
    },
    {
      id: 6,
      name: "Uploading Device",
      price: 2499,
      image: "/images/uploading.jpg"
    }
  ];

  useEffect(() => {
    API.get("/products")
      .then((res) => {
        if (res.data && res.data.length > 0) {
          const productsWithImages = res.data.map(product => ({
            ...product,
            image: product.image || getDefaultProductImage(product.id, product.name)
          }));
          setProducts(productsWithImages);
        } else {
          setProducts(sampleProducts);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("API failed, using sample data:", err);
        setProducts(sampleProducts);
        setLoading(false);
      });
  }, []);

  const getDefaultProductImage = (id, name) => {
    const colors = ['FF6B6B', '4ECDC4', '45B7D1', '96CEB4', 'FFEAA7', 'DDA0DD', '98D8C8', 'F7DC6F'];
    const color = colors[id % colors.length];
    const encodedName = encodeURIComponent(name || 'Product');
    return `https://via.placeholder.com/300x300/${color}/FFFFFF?text=${encodedName}`;
  };

  const addToCart = async (productId, productName) => {
    try {
      await API.post("/cart", { productId, qty: 1 });
      setSuccessMessage(`âœ… ${productName} added to cart!`);
      setTimeout(() => setSuccessMessage(""), 3000);
    } catch (error) {
      console.error("Failed to add item to cart:", error);
      alert("Failed to add item to cart. Please try again.");
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-white">
        <header className="bg-white border-b border-gray-100 sticky top-0 z-50">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center py-4">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-blue-600 rounded-lg flex items-center justify-center">
                  <span className="text-white text-lg">ðŸ›’</span>
                </div>
                <h1 className="text-2xl font-bold bg-gradient-to-br from-purple-500 to-blue-600 bg-clip-text text-transparent">
                  Vibe Commerce
                </h1>
              </div>
              <button 
                className="flex items-center space-x-2 px-6 py-3 bg-gradient-to-br from-purple-500 to-blue-600 text-white font-semibold rounded-full hover:shadow-lg transition-all duration-200"
                onClick={() => navigate("/cart")}
              >
                <span>ðŸ›’</span>
                <span>View Cart</span>
              </button>
            </div>
          </div>
        </header>

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold text-gray-900 mb-3">Discover Products</h2>
            <p className="text-xl text-gray-600">Loading amazing products for you...</p>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {[...Array(6)].map((_, index) => (
              <div key={index} className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden animate-pulse">
                <div className="h-60 bg-gray-200"></div>
                <div className="p-6">
                  <div className="h-4 bg-gray-200 rounded mb-3 w-3/4"></div>
                  <div className="h-6 bg-gray-200 rounded mb-4 w-1/2"></div>
                  <div className="h-12 bg-gray-200 rounded-xl"></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      {successMessage && (
        <div className="fixed top-6 right-6 bg-green-500 text-white px-6 py-4 rounded-xl shadow-lg flex items-center space-x-3 z-50 animate-slide-in">
          <span className="text-lg">âœ…</span>
          <span className="font-medium">{successMessage}</span>
        </div>
      )}

      <header className="bg-white border-b border-gray-100 sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-blue-600 rounded-lg flex items-center justify-center">
                <span className="text-white text-lg">ðŸ›’</span>
              </div>
              <h1 className="text-2xl font-bold bg-gradient-to-br from-purple-500 to-blue-600 bg-clip-text text-transparent">
                Vibe Commerce
              </h1>
            </div>
            <button 
              className="flex items-center space-x-2 px-6 py-3 bg-gradient-to-br from-purple-500 to-blue-600 text-white font-semibold rounded-full hover:shadow-lg transition-all duration-200 relative"
              onClick={() => navigate("/cart")}
            >
              <span>ðŸ›’</span>
              <span>View Cart</span>
              {products.length > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                  {products.length}
                </span>
              )}
            </button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">
            Discover Products
          </h2>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Explore our curated collection of amazing products
          </p>
        </div>

        {products.length === 0 ? (
          <div className="text-center py-20">
            <div className="text-6xl mb-6 opacity-50">ðŸ“¦</div>
            <h3 className="text-2xl font-semibold text-gray-900 mb-3">
              No products available
            </h3>
            <p className="text-gray-600 text-lg">
              We're restocking our shelves. Check back soon for new arrivals!
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {products.map((product) => (
              <div 
                key={product.id} 
                className="bg-white rounded-2xl shadow-sm hover:shadow-xl border border-gray-100 overflow-hidden group transition-all duration-300"
              >
                <div className="relative overflow-hidden bg-gray-100">
                  <img 
                    src={product.image} 
                    alt={product.name}
                    className="w-full h-60 object-cover transition-transform duration-300 group-hover:scale-105"
                    onError={(e) => {
                      e.target.src = getDefaultProductImage(product.id, product.name);
                    }}
                  />
                </div>

                <div className="p-6">
                  <h3 className="font-semibold text-gray-900 text-lg mb-2 line-clamp-2">
                    {product.name}
                  </h3>
                  <p className="text-2xl font-bold text-purple-600 mb-4">
                    â‚¹{product.price}
                  </p>
                  <button 
                    className="w-full bg-gradient-to-br from-purple-500 to-blue-600 text-white py-3 px-6 rounded-xl font-semibold hover:shadow-lg transition-all duration-200 flex items-center justify-center space-x-2"
                    onClick={() => addToCart(product.id, product.name)}
                  >
                    <span className="text-lg">+</span>
                    <span>Add to Cart</span>
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
