import React, { useState, useEffect } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";
import ReceiptModal from "./ReceiptModal";
import "./styles.css";

export default function CheckoutPage() {
  const [form, setForm] = useState({ name: "", email: "" });
  const [receipt, setReceipt] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ðŸ§  (Optional) Prefill user info if available from backend or localStorage
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setForm({ name: storedUser.name || "", email: storedUser.email || "" });
    }
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleCheckout = async () => {
    if (!form.name || !form.email) {
      alert("Please enter both name and email.");
      return;
    }

    try {
      setLoading(true);
      // ðŸªª Ensure JWT token is attached
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Please login before checkout.");
        navigate("/login");
        return;
      }

      // ðŸ›’ Get user cart (already filtered by user via backend)
      const cartRes = await API.get("/cart", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const payload = {
        name: form.name,
        email: form.email,
      };

      // ðŸ’³ Checkout request
      const res = await API.post("/checkout", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      console.log("Checkout success:", res.data);
      setReceipt(res.data);
      alert("Checkout successful!");
    } catch (err) {
      console.error("Checkout failed:", err);
      alert(err.response?.data?.error || "Checkout failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>

      <input
        type="text"
        name="name"
        placeholder="Full Name"
        value={form.name}
        onChange={handleChange}
      />

      <input
        type="email"
        name="email"
        placeholder="Email"
        value={form.email}
        onChange={handleChange}
      />

      <button onClick={handleCheckout} disabled={loading}>
        {loading ? "Processing..." : "Generate Bill"}
      </button>

      {receipt && (
        <ReceiptModal
          receipt={receipt}
          onConfirm={() => navigate("/ProductList")}
        />
      )}
    </div>
  );
}
