import "./styles.css";

export default function ReceiptModal({ receipt, onConfirm }) {
  return (
    <div className="receipt-modal">
      <div className="modal-content">
        <h3>Mock Receipt</h3>
        <p>Name: {receipt.name}</p>
        <p>Email: {receipt.email}</p>
        <p>Total: â‚¹{receipt.total}</p>
        <p>Time: {new Date(receipt.timestamp).toLocaleString()}</p>
        <button onClick={onConfirm}>Confirm Payment</button>
      </div>
    </div>
  );
}
