import React, { useEffect, useState } from "react";
import { ShoppingCart, Plus, TrendingUp } from "lucide-react";

export default function ProductList() {
  const [products, setProducts] = useState([
    {
      id: 1,
      name: "Wireless Headphones",
      price: 2999,
      image: "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400&h=400&fit=crop",
      tag: "Trending"
    },
    {
      id: 2,
      name: "Smart Watch",
      price: 4499,
      image: "https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=400&h=400&fit=crop",
      tag: "New"
    },
    {
      id: 3,
      name: "Laptop Backpack",
      price: 1299,
      image: "https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400&h=400&fit=crop"
    },
    {
      id: 4,
      name: "USB-C Cable",
      price: 399,
      image: "https://images.unsplash.com/photo-1583863788434-e58a36330cf0?w=400&h=400&fit=crop"
    },
    {
      id: 5,
      name: "Phone Stand",
      price: 699,
      image: "https://images.unsplash.com/photo-1600087626014-e652e18bbff2?w=400&h=400&fit=crop",
      tag: "Sale"
    },
    {
      id: 6,
      name: "Bluetooth Speaker",
      price: 1899,
      image: "https://images.unsplash.com/photo-1608043152269-423dbba4e7e1?w=400&h=400&fit=crop"
    }
  ]);
  const [cartCount, setCartCount] = useState(0);

  // Simulate API call
  useEffect(() => {
    // API.get("/products")
    //   .then((res) => setProducts(res.data))
    //   .catch((err) => console.error(err));
  }, []);

  const addToCart = async (productId) => {
    // await API.post("/cart", { productId, qty: 1 });
    setCartCount(prev => prev + 1);

    // Show toast notification
    const toast = document.createElement('div');
    toast.className = 'toast-notification';
    toast.textContent = '✓ Added to cart!';
    document.body.appendChild(toast);

    setTimeout(() => {
      toast.classList.add('show');
    }, 10);

    setTimeout(() => {
      toast.classList.remove('show');
      setTimeout(() => toast.remove(), 300);
    }, 2000);
  };

  const goToCart = () => {
    // navigate("/cart");
    alert('Navigate to cart page');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-white to-blue-50">
      <style>{`
        @keyframes slideDown {
          from {
            transform: translateY(-100%);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        @keyframes fadeInUp {
          from {
            transform: translateY(20px);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        @keyframes slideInRight {
          from {
            transform: translateX(100%);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }

        .toast-notification {
          position: fixed;
          top: 20px;
          right: 20px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          padding: 16px 24px;
          border-radius: 12px;
          box-shadow: 0 8px 16px rgba(0,0,0,0.2);
          transform: translateX(400px);
          opacity: 0;
          transition: all 0.3s ease;
          z-index: 1000;
          font-weight: 600;
        }

        .toast-notification.show {
          transform: translateX(0);
          opacity: 1;
        }

        .product-card {
          animation: fadeInUp 0.6s ease backwards;
        }

        .product-card:nth-child(1) { animation-delay: 0.1s; }
        .product-card:nth-child(2) { animation-delay: 0.2s; }
        .product-card:nth-child(3) { animation-delay: 0.3s; }
        .product-card:nth-child(4) { animation-delay: 0.4s; }
        .product-card:nth-child(5) { animation-delay: 0.5s; }
        .product-card:nth-child(6) { animation-delay: 0.6s; }

        .header-animate {
          animation: slideDown 0.6s ease;
        }

        .cart-badge {
          animation: slideInRight 0.5s ease;
        }
      `}</style>

      {/* Header */}
      <header className="header-animate bg-white/80 backdrop-blur-md shadow-sm sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-purple-600 to-blue-600 rounded-xl flex items-center justify-center">
                <TrendingUp className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent">
                  Vibe Commerce
                </h1>
                <p className="text-xs text-gray-500">Shop the latest trends</p>
              </div>
            </div>

            <button
              onClick={goToCart}
              className="cart-badge relative bg-gradient-to-r from-purple-600 to-blue-600 text-white px-6 py-3 rounded-full font-semibold shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-200 flex items-center space-x-2"
            >
              <ShoppingCart className="w-5 h-5" />
              <span>Cart</span>
              {cartCount > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs font-bold w-6 h-6 rounded-full flex items-center justify-center animate-bounce">
                  {cartCount}
                </span>
              )}
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Hero Section */}
        <div className="text-center mb-12">
          <h2 className="text-4xl font-bold text-gray-900 mb-3">
            Discover Amazing Products
          </h2>
          <p className="text-lg text-gray-600">
            Curated collection of trending items just for you
          </p>
        </div>

        {/* Product Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {products.map((product) => (
            <div
              key={product.id}
              className="product-card group bg-white rounded-2xl shadow-md hover:shadow-2xl transition-all duration-300 overflow-hidden transform hover:-translate-y-2"
            >
              {/* Product Image */}
              <div className="relative overflow-hidden bg-gray-100 aspect-square">
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                />
                {product.tag && (
                  <span className={`absolute top-4 right-4 px-3 py-1 rounded-full text-xs font-bold text-white shadow-lg ${
                    product.tag === 'Trending' ? 'bg-gradient-to-r from-orange-500 to-pink-500' :
                    product.tag === 'New' ? 'bg-gradient-to-r from-green-500 to-emerald-500' :
                    'bg-gradient-to-r from-red-500 to-rose-500'
                  }`}>
                    {product.tag}
                  </span>
                )}
              </div>

              {/* Product Details */}
              <div className="p-6">
                <h3 className="text-xl font-bold text-gray-900 mb-2 line-clamp-1">
                  {product.name}
                </h3>
                <div className="flex items-center justify-between mb-4">
                  <p className="text-2xl font-bold bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent">
                    ₹{product.price.toLocaleString()}
                  </p>
                  <span className="text-sm text-gray-500">Free Delivery</span>
                </div>

                {/* Add to Cart Button */}
                <button
                  onClick={() => addToCart(product.id)}
                  className="w-full bg-gradient-to-r from-purple-600 to-blue-600 text-white py-3 rounded-xl font-semibold shadow-md hover:shadow-lg transform hover:scale-[1.02] transition-all duration-200 flex items-center justify-center space-x-2 group"
                >
                  <Plus className="w-5 h-5 group-hover:rotate-90 transition-transform duration-300" />
                  <span>Add to Cart</span>
                </button>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-white/80 backdrop-blur-md mt-20 border-t border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 text-center text-gray-600">
          <p className="text-sm">© 2025 Vibe Commerce. Bringing you the best shopping experience.</p>
        </div>
      </footer>
    </div>
  );
}
